<template>
  <div :class="className" :style="{height:height,width:width}" />
</template>

<script>
import echarts from 'echarts'
require('echarts/theme/macarons') // echarts theme
import resize from './mixins/resize'

// 过去三年与本年的产量统计
import {statisticalProductMaryYears} from '@/api/index'

const animationDuration = 6000

export default {
  mixins: [resize],
  props: {
    className: {
      type: String,
      default: 'chart'
    },
    width: {
      type: String,
      default: '100%'
    },
    height: {
      type: String,
      default: '300px'
    }
  },
  data() {
    return {
      chart: null,
      mTest:['2020-1','2020-2','2020-3'],
      vTest:['3100','3200','3300'],
    }
  },
  mounted() {
    this.$nextTick(() => {
      this.statisticalProductMaryYears();
    })
  },
  beforeDestroy() {
    if (!this.chart) {
      return
    }
    this.chart.dispose()
    this.chart = null
  },
  methods: {
    /** 获取组别list **/
    statisticalProductMaryYears() {
      statisticalProductMaryYears().then(response => {
        var obj = response.data;
        var m = [];
        var v = [];
        for (let i = 0; i < obj.length; i++) {
          m[i] = obj[i].whenYears.substring(0, 4);
          v[i] = obj[i].prodVal;
        }
        this.initChart(m, v);
      })
    },
    initChart: function (m, v) {
      this.chart = echarts.init(this.$el, 'macarons')

      this.chart.setOption({
        title: {
          left: 'center',
          text: '', //标题文本
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: { // 坐标轴指示器，坐标轴触发有效
            type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'
          }
        },
        // toolbox: {
        //   right : '10px',
        //   feature: {
        //     magicType: {
        //       type: ['line', 'bar']
        //     }
        //   }
        // },
        grid: {
          top: 10,
          left: '2%',
          right: '2%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: [{
          type: 'category',
          data: m,
          axisTick: {
            alignWithLabel: true
          }
        }],
        yAxis: [{
          type: 'value',
          axisTick: {
            show: true
          }
        }],
        series: [{
          name: '年产量',
          type: 'pictorialBar',
          //xAxisIndex: 1,
          barCategoryGap: '-40%',
          //barCategoryGap: '-5%',
          symbol: 'path://d="M150 50 L130 130 L170 130  Z"',
          itemStyle: {
            normal: {
              color: function (params) {
                var colorList = [
                  'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)',
                  'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)',
                  'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)'
                ];
                return colorList[params.dataIndex];
              }
            },
            emphasis: {
              opacity: 1
            }
          },
          // type: 'bar',
          // stack: 'vistors',
           barWidth: '100%',
          data: v,
          label: {
            normal: {
              show: true,
              color: '#409EFF',
              position: 'top'
            }
          },

         // animationDuration
        },
        //  {
        //     name: '年产量',
        //     symbol: 'image://data:image/png;base64,',
        //     symbolSize: 42,
        //
        //     type: "line",
        //     yAxisIndex: 1,
        //     data: v,
        //     itemStyle: {
        //       normal: {
        //         borderWidth: 5,
        //         color: {
        //           colorStops: [{
        //             offset: 0,
        //             color: '#821eff'
        //           },
        //
        //             {
        //               offset: 1,
        //               color: '#204fff'
        //             }
        //           ],
        //         }
        //       }
        //     }
        //   }
        ]
      })
    }
  }
}
</script>
